package whut.bean;

import whut.util.StringUtil;

import javax.servlet.http.HttpServletRequest;
import java.util.Objects;
/**
 * 书籍类，存储书籍的基本信息：
 * 书籍的Id(书籍的主码)，书籍名，书籍作者，书籍出版社，书籍价格，书籍总库存，书籍剩余库存，书籍类别
 * 本图书管理系统规定当两个书籍的名字，作者，出版社都相同时，两个书籍就算是同一本书
 */
public class BookBean extends ItemBean {

    private String Type;        //书籍类别
    private String Img;
    private String Introduce;

    public final static String defaultImg = "data:image/jpeg;base64,";
    public final static String defaultIntro = "暂无简介";

    public BookBean() {
    }

    public String getType() {
        return Type;
    }

    public void setType(String type) {
        Type = type;
    }

    public String getImg() {
        return Img;
    }

    public void setImg(String img) {
        Img = img;
    }

    public String getIntroduce() {
        return Introduce;
    }

    public void setIntroduce(String introduce) {
        Introduce = introduce;
    }

    @Override
    public String toString() {
        return super.toString() + "BookBean{" +
                "Type='" + Type + '\'' +
                ", Img='" + Img + '\'' +
                ", Introduce='" + Introduce + '\'' +
                '}';
    }
}
